import React from 'react'
import {
    Card,
    CardImg,
    CardBody,
    CardTitle,
    CardSubtitle,
    CardText,
    Button
}from 'reactstrap'

import Fade from 'react-reveal/Fade'

import './about.css'


class aboutUs extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            text : ""
        }
    }

    container1(){
        return(
            <div id = 'aboutEvent'>
                        <Card>
                            <CardImg top height = '10%' width = '10%' src = "https://placeholdit.imgix.net/~text?txtsize=33&txt=318%C3%97180&w=318&h=180" alt="Card image cap1" />
                            <CardBody color>
                                <CardTitle>
                                    Events
                                </CardTitle>
                                <CardText>
                                    Talks from 
                                </CardText>
                                <Button> Click me</Button>
                            </CardBody>
                        </Card>
                    </div>
        )
    }

    container2(){
        return(
            <div id = 'aboutWorkshop'>
                        <Card>
                            <CardImg top width = '10%' src = "https://placeholdit.imgix.net/~text?txtsize=33&txt=318%C3%97180&w=318&h=180" alt="Card image cap1" />
                            <CardBody color>
                                <CardTitle>
                                    Workshops
                                </CardTitle>
                                <CardText>
                                    Full day informative workshops
                                </CardText>
                                <Button> Click me</Button>
                            </CardBody>
                        </Card>
                    </div>
        )
    }

    container3(){
        return(
            <div id = 'aboutProject'>
                        <Card>
                            <CardImg top width = '10%' src = "https://placeholdit.imgix.net/~text?txtsize=33&txt=318%C3%97180&w=318&h=180" alt="Card image cap1" />
                            <CardBody color>
                                <CardTitle>
                                    Projects
                                </CardTitle>
                                <CardText>
                                    Mind boggling projects and ideas
                                </CardText>
                                <Button> Click me</Button>
                            </CardBody>
                        </Card>
                    </div>
        )
    }
    render(){
        return(
            <><div><h1 id = 'abouth1'>About DSC PESU </h1></div>
            <div id = 'content'>
                <Fade bottom> {this.container1()}</Fade>
                <Fade bottom> {this.container2()}</Fade>
                <Fade bottom> {this.container3()}</Fade>
            </div></>
        );
    }
}

export default aboutUs
