import React from 'react'
import './about.css'

class aboutUs extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            text : 'We at DSC believe that everyone can code and make a difference. All it takes is will power and a slight push. DSC PESU emphasises on inclusivity. We provide our help to everyone irrespective of their background, department etc. We conduct workshops, talks and hackathos that is open to everyone in and around PESU. We also have projects led by a bunch of skilled individuals who recruit people based on the needs of the project. Come join DSC PESU and welcome new beginnings.',
            speed : 50,
            i : 0
        }
        this.typeWritter = this.typeWritter.bind(this);
    }

    typeWritter(){
        if (this.i < this.text.length){
            document.getElementById('text1').innerHTML += this.text.charAt(thi);
            this.i++;
            setTimeout(this.state.typeWritter, this.speed);
        }
    }
    render(){
        return(
            <div class = 'about'> 
                <div class="widget center">

                    <div class="blur"></div>

                         <div class="text center" id = 'text1'>
                            {this.typeWritter}
                        </div>

                    </div>
            </div>
        )
    }
}

export default aboutUs
